package com.example.searchHashtag.controllers;import com.example.searchHashtag.dto.CreateHashtagRequest;import com.example.searchHashtag.dto.CreateUserRequest;import com.example.searchHashtag.models.Hashtag;import com.example.searchHashtag.models.UserHashtag;import com.example.searchHashtag.services.HashtagService;import com.example.searchHashtag.services.UserAndHashtagService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;@RestControllerpublic class HashtagController {  @Autowired  HashtagService hashtagService;  @Autowired  UserAndHashtagService userAndHashtagService;  @PostMapping("/hashtags")  public Hashtag saveHashtag(    @RequestBody CreateHashtagRequest createHashtagRequest  ){    return hashtagService.saveHashtag(createHashtagRequest);  }  @GetMapping("/hashtags/{name}")  public List<UserHashtag> getHashtagsByEmail(          @PathVariable String name  ){    return userAndHashtagService.getHashtagsByName(name);  }}