package com.example.searchHashtag.controllers;import com.example.searchHashtag.dto.CreateHashtagRequest;import com.example.searchHashtag.dto.CreateUserRequest;import com.example.searchHashtag.models.Hashtag;import com.example.searchHashtag.services.HashtagService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestControllerpublic class HashtagController {  @Autowired  HashtagService hashtagService;  @PostMapping("/hashtags")  public Hashtag saveHashtag(    @RequestBody CreateHashtagRequest createHashtagRequest  ){    return hashtagService.saveHashtag(createHashtagRequest);  }}