package com.example.searchHashtag.controllers;import com.example.searchHashtag.dto.CreateUserRequest;import com.example.searchHashtag.models.User;import com.example.searchHashtag.services.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class UserController {  @Autowired  UserService userService;  @PostMapping("/users")  public User saveUser(    @RequestBody CreateUserRequest createUserRequest  ){    return userService.saveUser(createUserRequest);  }}