package com.example.searchHashtag.models;import javax.persistence.*;import java.io.Serializable;@Entity@Table(name = "TB_USER_HASHTAG")public class HashtagUser implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    public HashtagUser(Long user_id, Long hashtag_id) {        this.user_id = user_id;        this.hashtag_id = hashtag_id;    }    @Column()    private Long user_id;    @Column()    private Long hashtag_id;}