package com.example.searchHashtag.models;import javax.persistence.*;import java.io.Serializable;@Entity@Table(name = "TB_USER_HASHTAG")public class UserHashtag implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    @Column(name = "user_id")    private Long userId;    @Column(name = "hashtag_id")    private Long hashtagId;    public UserHashtag(Long UserId, Long hashtagId) {        this.userId = UserId;        this.hashtagId = hashtagId;    }    public UserHashtag() {}}