package com.example.searchHashtag.services;import com.example.searchHashtag.dto.CreateHashtagRequest;import com.example.searchHashtag.models.Hashtag;import com.example.searchHashtag.repositories.HashtagRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class HashtagService {  @Autowired  HashtagRepository hashtagRepository;  public Hashtag saveHashtag(CreateHashtagRequest createHashtagRequest){    Hashtag hashtag = new Hashtag(createHashtagRequest.getHashtag());    return hashtagRepository.save(hashtag);  }}