package com.example.searchHashtag.services;import com.example.searchHashtag.dto.CreateUserAndHashtagRequest;import com.example.searchHashtag.models.Hashtag;import com.example.searchHashtag.models.User;import com.example.searchHashtag.models.UserHashtag;import com.example.searchHashtag.repositories.UserAndHashtagRepository;import com.example.searchHashtag.repositories.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class UserAndHashtagService {  @Autowired  UserAndHashtagRepository userAndHashtagRepository;  @Autowired  UserRepository userRepository;  public UserHashtag saveUserAndHashtag(CreateUserAndHashtagRequest createUserAndHashtagRequest){    UserHashtag userHashtag = new UserHashtag(createUserAndHashtagRequest.getUserId(), createUserAndHashtagRequest.getUserId());    return userAndHashtagRepository.save(userHashtag);  }  public List<UserHashtag> getHashtagsByName(String name){    Optional<User> user = userRepository.findByName(name);    if(user.isEmpty()){      System.out.println("User not found!");    }    return userAndHashtagRepository.findAllByUserId(user.get().getId());  }}