package com.example.searchHashtag.services;import com.example.searchHashtag.dto.CreateUserRequest;import com.example.searchHashtag.models.User;import com.example.searchHashtag.repositories.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class UserService {  @Autowired  UserRepository userRepository;    public User saveUser(CreateUserRequest createUserRequest){      User user = new User(createUserRequest.getEmail(), createUserRequest.getAuthor(), createUserRequest.getName());      return userRepository.save(user);    }}